  import style from "./StudentLogin.module.css";

  import {NavLink , useHistory} from "react-router-dom";

   import {useState} from "react" ;
   import axios from "axios";
   import baseUrl from "../../baseUrl";


     function StudentLogin(){

        const [user , setUser] = useState({
            email:"",
            password:""
        });

        function onTextFieldChange(e){
            setUser({
                ...user ,
                [e.target.name] : e.target.value
            });
        }


          let history = useHistory();


      async function handleLogin()
       {
          let value  = await axios.get(`${baseUrl}/user/${user.email}`);

          //console.log(value.data.email);
          //console.log(user.email);

          //console.log(value.data.password);
          //console.log(user.password);

                if( value.data.email === user.email &&
                   value.data.password === user.password)
                {
                   alert("success");
                   sessionStorage.setItem("user" , user.email);
                   history.push("/StudentDashboard");
                }
                else alert(" Wrong User Email or password");
             
        }
        



         return(
            <div className="container">
            <div className="row justify-content-center">
              <div className="col-md-6">
                <form>
                  <div className="form-outline mb-4">
                    <input type="email" id="form2Example1" className="form-control"
                      onChange={(e) => onTextFieldChange(e)} />
                    <label className="form-label" htmlFor="form2Example1">
                      Email address
                    </label>
                  </div>
      
                  <div className="form-outline mb-4">
                    <input type="password" id="form2Example2" className="form-control" 
                    onChange={(e) => onTextFieldChange(e)}/>
                    <label className="form-label" htmlFor="form2Example2">
                      Password
                    </label>
                  </div>
      
                  <div className="row mb-4">
                    <div className="col d-flex justify-content-center">
                      <div className="form-check">
                        <input className="form-check-input" type="checkbox" value="" id="form2Example31" checked />
                        <label className="form-check-label" htmlFor="form2Example31">
                          Remember me
                        </label>
                      </div>
                    </div>
      
                    
                  </div>
      
                  <button type="button" className="btn btn-primary btn-block mb-4" onClick={handleLogin}>
                    Sign in
                  </button>
      
                  <div className="text-center">
                    <p>
                    <NavLink exact to="/StudentSignup"> Register</NavLink> 
                    </p>
                    <p>or sign up with:</p>
                    
                    <button type="button" className="btn btn-link btn-floating mx-1">
                      <i className="fab fa-facebook-f"></i>
                    </button>
      
                    <button type="button" className="btn btn-link btn-floating mx-1">
                      <i className="fab fa-google"></i>
                    </button>
      
                    <button type="button" className="btn btn-link btn-floating mx-1">
                      <i className="fab fa-twitter"></i>
                    </button>
      
                    <button type="button" className="btn btn-link btn-floating mx-1">
                      <i className="fab fa-github"></i>
                    </button>
                  </div>
                </form>
              </div>
            </div>
          </div>
         ); 
     }

     export default StudentLogin;
