import axios from "axios";
import React, { useState, useEffect } from "react";
import { useHistory, useParams } from "react-router-dom";
import style from "../StudentDashboard.module.css";
import baseUrl from "../../../baseUrl";

function Test() {
  let { id } = useParams();
  let { category } = useParams();

  const [allQuestions, setAllQuestions] = useState([]);
  const [answer, setAnswer] = useState({
    answer1: "",
    answer2: "",
    answer3: "",
    answer4: "",
    answer5: "",
  });

  useEffect(() => {
    async function getAllQuestions() {
      let value = await axios.get(`${baseUrl}/exam/${id}/question`);
      setAllQuestions(value.data);
    }
    getAllQuestions();
  }, [id]);

  function onRadioButtonChange(e) {
    setAnswer({
      ...answer,
      [e.target.name]: e.target.value,
    });
  }

  async function submitTest() {
    const correctAnswer = allQuestions.map((question) => question.answer);

    let score = 0;
    let status = "";

    if (correctAnswer[0] === answer.answer1) score++;
    if (correctAnswer[1] === answer.answer2) score++;
    if (correctAnswer[2] === answer.answer3) score++;
    if (correctAnswer[3] === answer.answer4) score++;
    if (correctAnswer[4] === answer.answer5) score++;

    if (score >= 3) status = "Pass";
    else status = "Fail";

    var date = new Date();
    var d = date.getDate() + "-" + (date.getMonth() + 1) + "-" + date.getFullYear();
    var t = date.getHours() + ":" + date.getMinutes() + ":" + date.getSeconds();

    let data = {
      status: status,
      score: score,
      email: { email: sessionStorage.getItem("user") },
      edate: d + " " + t,
      sname: { name: category },
      totalMarks: "5",
      examId: { id: id },
      totalQuestion: "5",
    };

    await axios.post(`${baseUrl}/result`, data);
    history.push("/StudentDashboard/Result");
  }

  let history = useHistory();

  return (
    <>
      <div className={`container ${style.displayBoxQuestionHeadingBox}`}>
        <div className="row justify-content-center">
          <div className={`col-md-8 text-center ${style.centerTitle}`}>
            <h1>Answer all the questions</h1>
          </div>
        </div>
      </div>
      {allQuestions.map((data, i) => {
        const count = i + 1;
        return (
          <div className={`container ${style.displayBoxQuestionBox}`} key={i}>
            <div className="row">
              <div className={`col-md-12 ${style.divQuestion}`}>
                <span>{data.qname}</span>
              </div>
            </div>
            <div className="row">
              <div className={`col-md-6 ${style.option}`}>
                <input
                  onChange={(e) => onRadioButtonChange(e)}
                  value={data.optionOne}
                  id={style.option1}
                  name={"answer" + count}
                  type="radio"
                />
                <label htmlFor="option1">{data.optionOne}</label>
              </div>
              <div className={`col-md-6 ${style.option}`}>
                <input
                  onChange={(e) => onRadioButtonChange(e)}
                  value={data.optionTwo}
                  id={style.option2}
                  name={"answer" + count}
                  type="radio"
                />
                <label htmlFor="option2">{data.optionTwo}</label>
              </div>
            </div>
            <div className="row">
              <div className={`col-md-6 ${style.option}`}>
                <input
                  onChange={(e) => onRadioButtonChange(e)}
                  value={data.optionThree}
                  id={style.option3}
                  name={"answer" + count}
                  type="radio"
                />
                <label htmlFor="option3">{data.optionThree}</label>
              </div>
              <div className={`col-md-6 ${style.option}`}>
                <input
                  onChange={(e) => onRadioButtonChange(e)}
                  value={data.optionFour}
                  id={style.option4}
                  name={"answer" + count}
                  type="radio"
                />
                <label htmlFor="option4">{data.optionFour}</label>
              </div>
            </div>
          </div>
        );
      })}
      <div className={`container ${style.submitExam}`}>
        <div className="row justify-content-center">
          <div className={`col-md-8 text-center`}>
            <button className={`btn btn-primary ${style.button}`} onClick={submitTest}>
              Submit Exam
            </button>
          </div>
        </div>
      </div>
    </>
  );
}

export default Test;
